import 'package:flutter_spinkit/flutter_spinkit.dart';import 'get_images.dart';import 'package:flutter/material.dart';import 'package:flutter_staggered_grid_view/flutter_staggered_grid_view.dart';import 'package:hive/hive.dart';import 'package:http/http.dart';import 'get_album.dart';class Images extends StatelessWidget {  ScrollController scrollController = ScrollController();  GetAlbum gallery;  Images(this.gallery);  Box box = Hive.box('photo');  Response response;  Future<List<GetAlbum>> getImages() async {    Response response;    box.clear();    if (box.isEmpty) {      response = await get(Uri.parse('https://jsonplaceholder.typicode.com/photos/?albumId=${gallery.id}'));      box.put('getphoto', response.body);    }    var getalbum = box.get('getphoto');    print(getalbum.toString());    return welFromJson(getalbum);  }  @override  Widget build(BuildContext context) {    return Scaffold(        backgroundColor: Color(0xff0F0B21),        appBar: AppBar(          title: Text('Title',            style: TextStyle(              fontFamily: 'Raleway',              fontSize: 20,            ),),          centerTitle: true,          backgroundColor: Color(0xff231d49),          toolbarHeight: 60,        ),        body: FutureBuilder(            future: getImages(),            builder: (context, snapshot) {              if(snapshot.hasData) {                return Padding(                    padding: const EdgeInsets.symmetric(horizontal: 16.0),                    child: SingleChildScrollView(                        scrollDirection: Axis.vertical,                        child: Column(                            children: <Widget>[                              SizedBox(height: 16),                              Container(                                  child: StaggeredGridView.countBuilder(                                    crossAxisCount: 3,                                    shrinkWrap: true,                                    scrollDirection: Axis.vertical,                                    itemCount: snapshot.data.length,                                    controller: scrollController,                                    itemBuilder: (context, index) {                                      // print(snapshot.data[index].url);                                      return Stack(                                        children: [                                          Container(                                              child: ClipRRect(                                                borderRadius: BorderRadius.circular(                                                    4.0),                                                child: Image.network(                                                    '${snapshot.data[index].url}'),                                              )                                          ),                                          Positioned(                                            bottom : 9,                                            left : 11,                                            child: Container(                                                width : (MediaQuery.of(context).size.width - 40) / 3 - 14,                                                child: Text(snapshot.data[index].title, style: TextStyle(                                                fontSize: 12,                                                fontFamily: 'Raleway',                                                fontWeight: FontWeight.w500,                                                color: Colors.white,                                            ),                                              overflow: TextOverflow.ellipsis,                                            )),                                          )                                        ],                                      );                                    },                                    staggeredTileBuilder: (int index) =>                                        StaggeredTile.                                        count(1, 1),                                    mainAxisSpacing: 6.0,                                    crossAxisSpacing: 6.0,                                  )                              ),                            ]                        )                    )                );              }              else{                return SpinKitFadingFour(                  color: Colors.white,                  size: 80,                );              }            }        )    );  }}